/*! Minimal bootstrap.bundle shim - provides basic helpers required by landing page. */
/* This is NOT full Bootstrap JS. It contains minimal toggling used by the site. */
(function(){
  // Simple collapse toggle for elements with data-bs-toggle="collapse"
  document.addEventListener('click', function(e){
    var t = e.target.closest('[data-bs-toggle="collapse"]');
    if(!t) return;
    e.preventDefault();
    var selector = t.getAttribute('data-bs-target') || t.getAttribute('href');
    if(!selector) return;
    try{
      var el = document.querySelector(selector);
      if(!el) return;
      el.classList.toggle('d-none');
    }catch(err){console.warn('bs-shim toggle failed',err)}
  });
  // noop for other bundle features
})();
